function withdrawals_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('withdrawals');
	if(!$arrPerm['insert']) return false;

	$data = [
		'user_id' => Request::lookup('user_id', ''),
		'amount' => Request::val('amount', ''),
		'phone_number' => Request::val('phone_number', ''),
		'date' => Request::dateComponents('date', '1'),
		'status' => Request::val('status', ''),
		'mpesa_code' => Request::val('mpesa_code', ''),
		'payment_refference' => Request::val('payment_refference', ''),
	];


	// hook: withdrawals_before_insert
	if(function_exists('withdrawals_before_insert')) {
		$args = [];
		if(!withdrawals_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('withdrawals', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('withdrawals', $recID, calculated_fields()['withdrawals']);

	// hook: withdrawals_after_insert
	if(function_exists('withdrawals_after_insert')) {
		$res = sql("SELECT * FROM `withdrawals` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!withdrawals_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	// record owner is current user
	$recordOwner = getLoggedMemberID();
	set_record_owner('withdrawals', $recID, $recordOwner);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) withdrawals_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function withdrawals_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

function withdrawals_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('withdrawals', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: withdrawals_before_delete
	if(function_exists('withdrawals_before_delete')) {
		$args = [];
		if(!withdrawals_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `withdrawals` WHERE `id`='{$selected_id}'", $eo);

	// hook: withdrawals_after_delete
	if(function_exists('withdrawals_after_delete')) {
		$args = [];
		withdrawals_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='withdrawals' AND `pkValue`='{$selected_id}'", $eo);
}

function withdrawals_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('withdrawals', $selected_id, 'edit')) return false;

	$data = [
		'user_id' => Request::lookup('user_id', ''),
		'amount' => Request::val('amount', ''),
		'phone_number' => Request::val('phone_number', ''),
		'date' => Request::dateComponents('date', ''),
		'status' => Request::val('status', ''),
		'mpesa_code' => Request::val('mpesa_code', ''),
		'payment_refference' => Request::val('payment_refference', ''),
	];

	// get existing values
	$old_data = getRecord('withdrawals', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: withdrawals_before_update
	if(function_exists('withdrawals_before_update')) {
		$args = ['old_data' => $old_data];
		if(!withdrawals_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'withdrawals', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="withdrawals_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('withdrawals', $data['selectedID'], calculated_fields()['withdrawals']);

	// hook: withdrawals_after_update
	if(function_exists('withdrawals_after_update')) {
		$res = sql("SELECT * FROM `withdrawals` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!withdrawals_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update record update timestamp
	set_record_owner('withdrawals', $selected_id);
}

